	public static String resolveExpression(String exp, int nextBegin) {
		// exp = solvePriority(exp);
		
		int begin = exp.length();
		if (!exp.contains("*")) {
			return exp;
		}

		String regex = "\\(([^\\(\\)]+[^\\|])\\)";

		// regex = "\\(([^\\(\\)]+\\|[^\\(\\)]+)+\\)";
		// regex="[^\\|\\(\\)\\+]\\(([^\\(\\)]+\\|[^\\(\\)]+)+\\)";
		Pattern pattern = Pattern.compile(regex);

		Matcher matcher = pattern.matcher(exp);

		if (matcher.find(nextBegin)) {

			////System.out.println("--");

			String match = matcher.group();
			//System.out.println("match1:" + match);

			int matcherIndex = exp.indexOf(match, nextBegin);

			nextBegin = matcherIndex + match.length();
			boolean shouldContinue = true;
			if (nextBegin < exp.length()) {
				if ((exp.charAt(nextBegin) == '+' || exp.charAt(nextBegin) == '-')) {

					shouldContinue = false;
					if (matcherIndex - 1 > -1) {
						if (exp.charAt(matcherIndex - 1) == '*') {
							shouldContinue = true;
						}
					}
				}
			}
			if (matcherIndex - 1 > -1) {
				if (exp.charAt(matcherIndex - 1) == '+' || exp.charAt(matcherIndex - 1) == '-') {
					shouldContinue = false;
					if (nextBegin < exp.length()) {
						if (exp.charAt(nextBegin) == '*') {
							shouldContinue = true;
						}
					}
				}
			}
			if (shouldContinue) {
				char preChar;
				char sufChar;
				if (matcherIndex > 0 && (preChar = exp.charAt(matcherIndex - 1)) != '|' && preChar != '('
						&& preChar != ')' && preChar != '+' && preChar != '-') {

					match = preChar + match;

					matcherIndex = matcherIndex - 1;
					nextBegin++;
					for (int k = matcherIndex - 1; k >= 0; k--) {
						preChar = exp.charAt(k);
						if (preChar != '+' && preChar != '-' && preChar != '(' && preChar != ')' && preChar != '|') {

							match = preChar + match;
						} else {
							break;
						}
					}
					//System.out.println("match1:" + match);
					exp = exp.replace(match, transform(match));
				} else if (matcherIndex + match.length() < exp.length()
						&& (sufChar = exp.charAt(matcherIndex + match.length())) != '|' && sufChar != '('
						&& sufChar != ')' && sufChar != '+' && sufChar != '-') {
					match = match + sufChar;
					matcherIndex = exp.indexOf(match) + match.length();

					nextBegin++;
					for (int k = matcherIndex; k < exp.length(); k++) {
						sufChar = exp.charAt(k);
						if (sufChar != '+' && sufChar != '(' && sufChar != ')' && sufChar != '-' && sufChar != '|') {
							nextBegin++;
							match = match + sufChar;
						} else {
							break;
						}
					}
					//System.out.println("match2:" + match);
					exp = exp.replace(match, transform(match));
				}
			}

		}
		//System.out.println("exp2："+exp);
		
		
		
		int before = exp.length();
		exp = removeDuplicateInbracket(exp);
		exp = resolveDoubleInbracket(exp);
		exp = removeDuplicateInbracket(exp);
	  //System.out.println("exp3："+exp);
		int after = exp.length();
		//System.out.println(before+","+after);
		if (begin > after) {
			//nextBegin = 0;
			//System.out.println("set nextBegin to 0");
		}

		matcher = pattern.matcher(exp);
		//System.out.println("nextBegin:" + nextBegin);
		if (matcher.find(nextBegin)) {
			return resolveExpression(exp, nextBegin);
		}
		return exp;
	}
	
	public static String removeBracket(String exp){
		exp = exp.trim();
		int length = exp.length();
		if(exp.endsWith(")")&&exp.indexOf("(")==0&&exp.indexOf("(", 1)==-1){
			exp=exp.substring(1,exp.length()-1);
		}
		if(exp.length()<length){
			return removeBracket(exp);
		}else{
			return exp;
		}
	}

	public static String format(String exp) {
		exp = removeBracket(exp);
		String content = exp;
		int startIndex = -1;
		int endIndex = -1;
		int inbracket = 0;
		List<String> keywords = new ArrayList<String>();
		startIndex = 0;
		for (int k = 0; k < content.length(); k++) {

			String c = content.substring(k, k + 1);
			if ("(".equals(c)) {
				inbracket++;
			}
			if (")".equals(c)) {
				inbracket--;
			}
			if ("|".equals(c) && inbracket == 0) {
				endIndex = k;
				keywords.add(content.substring(startIndex, endIndex));
				startIndex = endIndex + 1;
			}

		}
		keywords.add(content.substring(endIndex + 1));
		StringBuilder sb = new StringBuilder();
		for (String keyword : keywords) {
			if (!sb.toString().isEmpty()) {
				sb.append("|");

			}
			sb.append("[").append(keyword).append("]");
		}
		return sb.toString();
	}

	
	public static List<String> split(String exp) {
		exp = removeBracket(exp);
		String content = exp;
		int startIndex = -1;
		int endIndex = -1;
		int inbracket = 0;
		List<String> keywords = new ArrayList<String>();
		startIndex = 0;
		for (int k = 0; k < content.length(); k++) {

			String c = content.substring(k, k + 1);
			
			if ("(".equals(c)) {
				inbracket++;
			}
			if (")".equals(c)) {
				inbracket--;
			}
			if ("|".equals(c) && inbracket == 0) {
				endIndex = k;
				keywords.add(content.substring(startIndex, endIndex));
				startIndex = endIndex + 1;
			}

		}
		
		String keyword=content.substring(endIndex + 1);
		if(keyword.contains(")")&&!keyword.contains("(")){
			keyword = keyword.replace(")", "");
		}
		if(keyword.contains("(")&&!keyword.contains(")")){
			keyword = keyword.replace("(", "");
		}
		keywords.add(keyword);
		
		return keywords;
	}

	
	
	public static String x(String exp) {
		
		return resolveExpression(exp, 0);
	}

